import express from 'express';
import {ratingAppointment, createMoMoPayment, inputUrl, changePassword, forgotPassword, loginUser, registerUser,addUser, getProfile, updateProfile, bookAppointment, listAppointment, editAppointment, cancelAppointment, paymentRazorpay, verifyRazorpay, paymentStripe, verifyStripe, allSlotUser, userList, editUser } from '../controllers/userController.js';
import upload from '../middleware/multer.js';
import authUser from '../middleware/authUser.js';
const userRouter = express.Router();

userRouter.get('/createTable', async (req, res) => {
  const sql = `
      CREATE TABLE IF NOT EXISTS users (
        id INT AUTO_INCREMENT PRIMARY KEY,
        name VARCHAR(255) NOT NULL,
        email VARCHAR(255) NOT NULL UNIQUE,
        phone VARCHAR(15) DEFAULT '000000000',
        address VARCHAR(255) NULL,
        gender VARCHAR(50) DEFAULT 'Not Selected',
        dob VARCHAR(50) DEFAULT 'Not Selected',
        password VARCHAR(255) NOT NULL,
        image TEXT DEFAULT 'data:image/png;base64,'
      )
  `;

  try {
    await req.app.locals.db.execute(sql);
    res.send('Bảng users đã được tạo hoặc đã tồn tại');
  } catch (err) {
    console.error(err);
    res.status(500).send('Đã xảy ra lỗi khi tạo bảng');
  }
});


userRouter.post("/register", registerUser)
userRouter.post("/add-user", addUser)
userRouter.post("/edit-user", editUser)
userRouter.get("/list", userList)
userRouter.post("/login", loginUser)
userRouter.post("/forgot-password", forgotPassword)
userRouter.post("/update-password", authUser, changePassword)
userRouter.get("/get-profile", authUser, getProfile)
userRouter.post("/update-profile", upload.single('image'), authUser, updateProfile)
userRouter.post("/book-appointment", authUser, bookAppointment)
userRouter.get("/appointments", authUser, listAppointment)
userRouter.post("/cancel-appointment", authUser, cancelAppointment)
userRouter.post("/edit-appointment", authUser, editAppointment)
userRouter.post("/payment-razorpay", authUser, paymentRazorpay)
userRouter.post("/verifyRazorpay", authUser, verifyRazorpay)
userRouter.post("/payment-stripe", authUser, paymentStripe)
userRouter.post("/verifyStripe", authUser, verifyStripe)
userRouter.post("/momo/create-payment", authUser, createMoMoPayment)
userRouter.post("/momo/ipn", authUser, inputUrl)
userRouter.post("/rating", authUser, ratingAppointment)
userRouter.get("/all-slot-user", allSlotUser)

export default userRouter;